import React  from 'react';
import {BrowserRouter, Route , Routes } from 'react-router-dom'
import './App.css';
// import Dashboard from './components/Dashboard/Dashboard';
import Login from './components/Login/Login';
import UseToken from './UseToken';

//  const Log = React.lazy(()=> import ('./components/Login/Login'))


// function setToken(userToken) {
//   sessionStorage.setItem('token', JSON.stringify(userToken));
// }


// function getToken() {
//   const tokenString = sessionStorage.getItem('token');
//   const userToken = JSON.parse(tokenString);
//   return userToken?.token
// }

const Dash = React.lazy(()=> import('./components/Dashboard/Dashboard')) 

function App() {
  // // const token = getToken();
  const { token, setToken } = UseToken();
  if(!token) {
    return <Login setToken={setToken} />
  }

  return (
      <BrowserRouter>
      <Routes>
      <Route path='/' element={<React.Suspense fallback={<div >
          <img style={{marginLeft:'40rem',marginTop:'15rem'}}src={require('./Spin-1s-200px.gif')} alt='hello'/>
        </div>}>
              <Dash />
        </React.Suspense>
}/>
      </Routes>
      </BrowserRouter>
  );
}

export default App;
